	}
	return (
		<Container>

			<Card>
				<Grid container>
					<Grid>
						<Typography>Bienvenido</Typography>
					</Grid>
					<Grid>
						<label>icono</label>
					</Grid>
				</Grid>
				<TextField
					value={searchText}
					placeholder="Buscar..."
					onChange={handleSearchTextChange}
				/>
				<Grid>
					<Button variant="contained" onClick={handleCleanTextClick} >clean</Button>
					<Button variant="contained" color="primary" onClick={handleSearchTextClick} > search</Button>
				</Grid>

			</Card>

		</Container>


	)
}
